parameters.jsCode:
const input = items[0].json.output;

const updated = input.map(field => ({
  ...field,
  deal_type: $('Trigger').first().json.deal_type
}));

return updated;

parameters.query:
SELECT id, metadata->>'uuid' AS uuid, text, metadata
FROM deals_newfields_embeddings
WHERE metadata->>'uuid' = '{{ $json.metadata.uuid }}' AND metadata->>'deal_type' = '{{ $json.metadata.deal_type }}';

parameters.inputSchema:
{
  "type": "array",
  "description": "List of 10 new fields recommended for future schema extensions. These fields are inferred from the Deal Document, excluding already extracted fields. Each recommended field is actionable and specific, avoiding descriptive or narrative-style content. These recommendations aim to support data modeling and automation use cases.",
  "items": {
    "type": "object",
    "properties": {
      "field_name": {
        "type": "string",
        "description": "A concise and descriptive name for the new field to be extracted."
      },
      "field_description": {
        "type": "string",
        "description": "A clear explanation of the purpose and meaning of this field, based on document analysis."
      },
      "field_tag": {
        "type": "string",
        "description": "A unique machine-friendly identifier for this field, usually lowercase with underscores."
      },
      "associated_to": {
        "type": "string",
        "description": "The entity in the schema this field belongs to. Choose from: 'deal', 'agreement', or 'credit'. For your reference, a deal is a single documents which could contain one or more agreements. An agreement may contain one or more credits.",
        "enum": ["deal", "agreement", "credit"]
      },
      "exist_in_db": {
        "type": "boolean",
        "description": "Indicates whether the field already exists in the database based on the result of tool:CheckField."
      },
      "uuid": {
        "type": ["string", "boolean"],
        "description": "The UUID of the field if it exists in the database(extracted from tool:CheckField payload); otherwise, set to false."
      }
    },
    "required": ["field_name", "field_description", "field_tag", "associated_to", "exist_in_db", "uuid"],
    "additionalProperties": false
  }
}
