const action = $input.item.json.data || {};

// Only process if status is 'pending'
if (action.status === 'pending') {
  const toEmails = (action.to || []).join(', ');
  const manager = (action.managers && action.managers[0]) || {};
  const referenceId = manager.MessageId || '<no-message-id>';
  const references = (manager.References || []).join(' ');
  const subject = action.Subject || 'No Subject';
  const threadId = action.ThreadId;
  const body = action.ask_manager || '';

  // Compose MIME message
  const mime = `To: ${toEmails}
Subject: ${subject}
In-Reply-To: ${referenceId}
References: ${references}
Content-Type: text/plain; charset=UTF-8

${body}`;

  // Encode to base64url
  const raw = Buffer.from(mime)
    .toString('base64')
    .replace(/\+/g, '-')
    .replace(/\//g, '_')
    .replace(/=+$/, '');

  // Attach email payload to the action
  action.email_payload = {
    raw,
    mime,
    threadId,
    subject,
    to: toEmails
  };
}

// ✅ Return the modified single item
return { json: action };

