const input = $input.first()?.json ?? [];
// Handle both single object and array
const data = Array.isArray(input) ? input : [input];
const responses = data.map(item => {
  const {
    person_legal_name,
    person_performer_name,
    book_number,
    book_id,
    verification_status
  } = item;
  // Helper function to check if value exists and is not empty
  const hasValue = (value) => value && value !== null && value.toString().trim() !== '';
  // Start building the response
  let response = '';

  if (verification_status === true) {
    // Verified case
    if (hasValue(person_legal_name)) {
      response = `The ${person_legal_name}`;

      // Add performer name if it exists and is different from legal name
      if (hasValue(person_performer_name) && person_performer_name !== person_legal_name) {
        response += ` also referred to as ${person_performer_name}`;
      }

      response += ' is verified in the Manager\'s workspace.';

      // Add verification details
      let details = [];
      details.push(`Legal Name: ${person_legal_name}`);

      if (hasValue(person_performer_name)) {
        details.push(`Performer Name: ${person_performer_name}`);
      }

      if (hasValue(book_number)) {
        details.push(`book_number: ${book_number}`);
      }

      if (hasValue(book_id)) {
        details.push(`book_id: ${book_id}`);
      }

      details.push(`Verification Status: verified`);

      response += '\n\nDetails:\n' + details.join('\n');
    } else {
      response = 'Person is verified in the Manager\'s workspace but no legal name provided.';
    }
  } else {
    // Not verified case
    if (hasValue(person_legal_name)) {
      response = `The ${person_legal_name}`;

      if (hasValue(person_performer_name) && person_performer_name !== person_legal_name) {
        response += ` also referred to as ${person_performer_name}`;
      }

      response += ' is not verified in the Manager\'s workspace.';

      // Add basic details (excluding book_number and book_id for unverified)
      let details = [];
      details.push(`Legal Name: ${person_legal_name}`);

      if (hasValue(person_performer_name)) {
        details.push(`Performer Name: ${person_performer_name}`);
      }

      details.push(`Verification Status: not_verified`);

      response += '\n\nDetails:\n' + details.join('\n');
    } else {
      response = 'Person is not verified and no legal name provided.';
    }
  }

  return response;
});
// Return the formatted responses
return responses.map(response => ({ json: { message: response } }));

