// Extract the last email object from the Switch node
const lastEmail = $('EmailPath').first().json || {};

// ---------- Helpers ----------
const htmlDecode = (s = "") =>
  s
    .replace(/&nbsp;/gi, " ")
    .replace(/&amp;/gi, "&")
    .replace(/&lt;/gi, "<")
    .replace(/&gt;/gi, ">")
    .replace(/&quot;/gi, '"')
    .replace(/&#39;/gi, "'")
    .replace(/&#x2F;/gi, "/");

const htmlToText = (html = "") => {
  if (!html) return "";
  let s = html;
  // Remove scripts/styles
  s = s.replace(/<script[\s\S]*?<\/script>/gi, "")
       .replace(/<style[\s\S]*?<\/style>/gi, "");
  // Block-level breaks → newlines
  s = s.replace(/<(br|hr)\s*\/?>/gi, "\n")
       .replace(/<\/(p|div|li|h[1-6]|tr|table)>/gi, "\n")
       .replace(/<(p|div|li|h[1-6]|tr|table)[^>]*>/gi, " ");
  // Remove all remaining tags
  s = s.replace(/<\/?[^>]+>/g, "");
  // Decode entities & normalize whitespace
  s = htmlDecode(s).replace(/\r/g, "").replace(/[ \t\u00A0]+/g, " ");
  return s;
};

const cutAtFirst = (text, patterns) => {
  let cut = -1;
  for (const re of patterns) {
    const idx = text.search(re);
    if (idx !== -1 && (cut === -1 || idx < cut)) cut = idx;
  }
  return cut === -1 ? text : text.slice(0, cut);
};

const stripSignature = (text) => {
  // Find earliest likely signature delimiter
  const sigPatterns = [
    /\n--\s*\n/i,                                  // RFC-ish
    /\nSent from my (iPhone|Android).*$/i,        // mobile footers
    /\nBest regards[,\s]*\n.*$/i,
    /\nRegards[,\s]*\n.*$/i,
    /\nThanks[,\s]*\n.*$/i
  ];
  const cutIdx = sigPatterns
    .map(re => text.search(re))
    .filter(i => i !== -1)
    .sort((a,b) => a - b)[0];
  return (cutIdx !== undefined) ? text.slice(0, cutIdx) : text;
};

const cleanWhitespace = (s) =>
  s.replace(/\u200B/g, "")        // zero-width space
   .replace(/\r/g, "")
   .replace(/[ \t]+/g, " ")
   .replace(/\n{3,}/g, "\n\n")
   .trim();

const firstBodyOnly = (s) => {
  // Normalize line endings for pattern matching
  const t = s.replace(/\r\n/g, "\n");

  // Cut off quoted/forwarded/reply headers
  const cutoffPatterns = [
    /\n\s*On .*? wrote:\s*\n/mi,                         // Gmail/Apple "On … wrote:"
    /\n\s*-{2,}\s*Original Message\s*-{2,}\s*\n/mi,      // -----Original Message-----
    /\n\s*Begin forwarded message:\s*\n/mi,              // Apple Mail
    /\n\s*-{2,}\s*Forwarded message\s*-{2,}\s*\n/mi,     // ---------- Forwarded message ----------
    /\n>+/m,                                             // First quoted line
    // Header block: From/Sent/To/Subject cluster
    /\nFrom:\s.*\n(?:.*\n){0,12}?Subject:\s.*\n/mi
  ];

  let newest = cutAtFirst(t, cutoffPatterns);

  // Also cut custom separators you mentioned
  newest = cutAtFirst(newest, [
    /<asst@patchbay\.xyz>/i,
    /Forwarded message/i
  ]);

  // Remove signature and tidy whitespace
  newest = stripSignature(newest);
  return cleanWhitespace(newest);
};

const pickBody = (email) => {
  // Prefer plain text if present; otherwise, convert HTML
  const text = (email.text && email.text.trim()) ? email.text : "";
  const html = (!text && email.html) ? email.html : "";
  return text ? text : htmlToText(html);
};

const extractAddresses = (field) =>
  (lastEmail?.[field]?.value || [])
    .map(e => (e && typeof e === "object") ? e.address : e)
    .filter(Boolean);

// ---------- Build result ----------
let raw = pickBody(lastEmail);
let emailText = firstBodyOnly(raw);

// Final normalization to a single-line-ish text (optional; keep line breaks by removing the next line)
emailText = emailText.replace(/\n/g, " ").replace(/\s+/g, " ").trim();

return [
  {
    json: {
      ThreadId: lastEmail.threadId || null,
      email_text: emailText,
      from: extractAddresses("from"),
      to: extractAddresses("to"),
      cc: extractAddresses("cc"),
      bcc: extractAddresses("bcc"),
      manager: $input.first().json.manager
    }
  }
];

