// n8n Function node
// Input: last 10 message objects, newest at index 0
// Output: { json: { markdown: "<markdown string>" } }

const WINDOW_SIZE = 10;

// ---- Helpers ----
const toStr = (v) => (v === null || v === undefined) ? "" : String(v);

const stripHtml = (s) => {
  const str = toStr(s);
  return str
    .replace(/<style[\s\S]*?<\/style>/gi, " ")
    .replace(/<script[\s\S]*?<\/script>/gi, " ")
    .replace(/<[^>]*>/g, " ")
    .replace(/\s+/g, " ")
    .trim();
};

const decodeHtml = (s) => {
  const map = {
    "&nbsp;": " ",
    "&lt;": "<",
    "&gt;": ">",
    "&amp;": "&",
    "&quot;": "\"",
    "&#39;": "'",
  };
  return toStr(s).replace(/(&nbsp;|&lt;|&gt;|&amp;|&quot;|&#39;)/g, (m) => map[m] || m);
};

const htmlToText = (html) => stripHtml(decodeHtml(html));

const safeJsonParse = (val) => {
  if (typeof val !== "string") return null;
  const t = val.trim();
  if (!(t.startsWith("{") || t.startsWith("["))) return null;
  try { return JSON.parse(t); } catch { return null; }
};

const formatTime = (raw) => {
  if (!raw) return "unknown";
  // If it's already a readable string (e.g., "2025-09-05 10:23:44"), keep it.
  // Otherwise try to parse and convert to ISO.
  const d = new Date(raw);
  if (isNaN(d.getTime())) return toStr(raw).trim();
  // Use local-like ISO without milliseconds for readability
  const iso = d.toISOString().replace("T", " ").replace(".000Z", "Z");
  return iso;
};

// ---- Collect messages (use order as provided; newest first) ----
let messages = [];
if (items.length === 1) {
  const j = items[0].json ?? {};
  if (Array.isArray(j)) messages = j;
  else if (Array.isArray(j.data)) messages = j.data;
  else if (Array.isArray(j.messages)) messages = j.messages;
  else if (j.id && j.message) messages = [j];
} else {
  for (const it of items) {
    const j = it.json ?? {};
    if (j.id && j.message) messages.push(j);
  }
}

if (!messages.length) {
  return [{ json: { history: "Missing" } }];
}

// ---- Keep window of 10 (already newest-first), then switch to chronological for pairing ----
const windowMsgs = messages.slice(0, WINDOW_SIZE);
const chronological = windowMsgs.slice().reverse(); // oldest -> newest

// ---- Build user→AI turns (with timestamps) ----
const turns = [];
for (let i = 0; i < chronological.length; i++) {
  const msg = chronological[i];
  if (msg?.message?.type === "human") {
    const userRaw = msg.message.content ?? "";
    const userText = stripHtml(userRaw);

    let aiText = null;

    // Look ahead for next AI reply within the window
    for (let j = i + 1; j < chronological.length; j++) {
      const next = chronological[j];
      const type = next?.message?.type;
      if (type === "ai") {
        const raw = next.message.content ?? "";
        const parsed = safeJsonParse(raw);
        const responseHtml = parsed?.output?.response_html ?? null;
        aiText = responseHtml ? htmlToText(responseHtml) : stripHtml(raw);
        break;
      }
      if (type === "human") break; // next human starts a new turn
    }

    turns.push({
      userText,
      aiText,
    });
  }
}

// ---- Build Markdown string ----
let md = `### Conversation (last ${Math.min(WINDOW_SIZE, turns.length)} turns)\n\n`;
turns.forEach((t) => {
  md += `**User** ${t.userText}\n\n`;
  if (t.aiText) {
    md += `**AI** ${t.aiText}\n\n`;
  } else {
    md += `**AI** (no reply found)_\n\n`;
  }
});

return [{
  json: {
    history: md.trim()
  }
}];

// Get the reply ID to match
const inReplyTo = $('EmailPath').first()?.json?.inReplyTo;

// Scan all incoming items
const items = $input.all();

let matched = null;

for (const it of items) {
  const sessionStr = it.json?.user_session;
  if (typeof sessionStr !== 'string') continue;

  try {
    const sessionObj = JSON.parse(sessionStr);
    if (sessionObj?.routing_id && sessionObj.routing_id === inReplyTo) {
      matched = sessionObj;
      break;
    }
  } catch (e) {
    // skip invalid JSON
  }
}

return [
  {
    json: {
      output: matched
    }
  }
];

// Parse, extract base64 PDF, attach as binary
// Works with multiple incoming items.

const items = $input.all();

function safeParseSession(value) {
  if (value && typeof value === 'object') return value;
  if (typeof value === 'string') {
    try {
      return JSON.parse(value);
    } catch (err) {
      return { __parse_error: err.message, __raw: value };
    }
  }
  return { __parse_error: 'Unsupported user_session type', __raw: value };
}

const outputs = [];
for (const item of items) {
  const parsedSession = safeParseSession(item.json.user_session);

  const out = {
    json: {
      ...item.json,
      user_session: parsedSession,
    },
  };

  // Try to pull base64 from user_session.invoice_metadata.pdf_invoice_content
  const pdfB64 = parsedSession?.invoice_metadata?.pdf_invoice_content;

  if (typeof pdfB64 === 'string' && pdfB64.trim().length > 0) {
    try {
      const buf = Buffer.from(pdfB64.trim(), 'base64');

      // Name the file using invoice_id if available
      const invoiceId = parsedSession?.invoice_metadata?.invoice_id ?? 'invoice';
      const fileName = `invoice_${invoiceId}.pdf`;

      // Prepare binary for n8n
      const binary = await this.helpers.prepareBinaryData(
        buf,
        fileName,
        'application/pdf'
      );

      out.binary = { pdf_invoice: binary };

      // Optional: remove the large base64 string from JSON to keep items light
      try {
        if (out.json.user_session?.invoice_metadata) {
          delete out.json.user_session.invoice_metadata.pdf_invoice_content;
        }
      } catch (_) {}
    } catch (err) {
      out.json.__binary_error = `Failed to decode PDF: ${err.message}`;
    }
  } else {
    out.json.__binary_error = 'pdf_invoice_content not found or not a string';
  }

  outputs.push(out);
}

return outputs;

// Step 1: Input values
const item = $('GetLatestSession2').first().json

const messageId = $('GetMessageId1').first()?.json?.messageId ?? '';
const lastResponse = $('CrudSupervisor').first()?.json?.output.response ?? '';

