let userSessionObj;
try {
  if (typeof item.user_session !== 'string') {
    throw new Error("Expected 'user_session' to be a string.");
  }
  userSessionObj = JSON.parse(item.user_session);
} catch (err) {
  throw new Error("Invalid JSON in 'user_session' field.");
}

// Step 3: Update the user_session (no sub_sessions map here)
userSessionObj = {
  ...userSessionObj,
  routing_id: messageId,
  last_bot_response: lastResponse
};

// Step 4: Return the updated user_session (stringified)
return [
  {
    json: {
      ...item,
      user_session: userSessionObj
    }
  }
];

