const items = $('Trigger').all();

for (const item of items) {
  const headers = item.json.payload?.headers || [];
  const html = item.json.payload?.body?.data || item.json.html || "";

  // Ensure headers is an array
  if (!Array.isArray(headers)) {
    item.json.isForwarded = false;
    continue;
  }

  // Create header map
  const headerMap = {};
  for (const h of headers) {
    if (h.name && h.value) {
      headerMap[h.name.toLowerCase()] = h.value;
    }
  }

  // Known forwarding headers
  const forwardedHeaders = [
    "x-forwarded-for",
    "x-forwarded",
    "x-forwarded-encrypted",
    "x-mail-forwarded-for"
  ];

  // Base check: known headers
  let isForwarded = forwardedHeaders.some(h => h in headerMap);

  // Check for common forwarded message marker in content
  if (typeof html === 'string' && html.includes("---- Forwarded message ----")) {
    isForwarded = true;
  }

  // Check if HTML contains more than 4 email addresses
  const emailRegex = /[a-zA-Z0-9._%+-]+@[a-zA-Z0-9.-]+\.[a-z]{2,}/gi;
  const emailMatches = html.match(emailRegex);
  if (emailMatches && emailMatches.length > 4) {
    isForwarded = true;
  }

  item.json.isForwarded = isForwarded;
}
return items;

const results = [];

for (const item of $input.all()) {
  const data = item.json;

  // Get all CCs except 'asst@patchbay.xyz'
  const allCC = data.cc?.value || [];
  const filteredCC = allCC.filter(cc => cc.address !== "asst@patchbay.xyz");

  const output = {
    ThreadId: data.threadId,
    EmailId : data.id,
    MessageId: data.messageId,
    References: data.references,
    Subject: data.subject,
    From: data.from?.value || [],
    to: data.to?.value || [],
    cc: filteredCC,
    date: data.date,
    emailText: data.text || '',
    isForwarded: data.isForwarded || false
  };

  results.push({ json: output });
}

return results;

