const input = $input.all()[0];
const data = input.json;

const seen = new Set();
const uniqueEmails = [];

for (const email of data.Emails || []) {
  ['From', 'to', 'cc'].forEach(field => {
    const list = email[field] || [];
    list.forEach(entry => {
      if (entry.address) {
        const address = entry.address.toLowerCase();
        if (!seen.has(address)) {
          seen.add(address);
          uniqueEmails.push({
            name: entry.name || "",
            address: address
          });
        }
      }
    });
  });
}

data.Others = uniqueEmails;

return [{ json: data }];

// Step 1: Collect emails from all items
const allEmails = items.flatMap(item => item.json.output?.emails || []);

// Step 2: Get shared metadata from ExtractData node
const threadId = $('ExtractData').first().json.ThreadId;
const newEmailId = $('ExtractData').first().json.EmailId;
const msgId = $('ExtractData').first().json.MessageId;
const ref = $('ExtractData').first().json.References;

// Step 3: Build structured output
const output = {
  ThreadId: threadId,
  Emails: allEmails.map(email => ({
    ...email,
    EmailId: newEmailId,
    MessageId: msgId,
    References: ref
  }))
};

// Step 4: Return as single object
return [{ json: output }];

