const input = $input.all().map(item => item.json);

const grouped = {};

for (const email of input) {
  const threadId = email.ThreadId;

  if (!grouped[threadId]) {
    grouped[threadId] = {
      ThreadId: threadId,
      Emails: []
    };
  }

  // Clone email and remove isForwarded
  const {
    isForwarded, // omit this
    ThreadId,    // omit this from child
    ...cleanedEmail
  } = email;

  grouped[threadId].Emails.push(cleanedEmail);
}

return Object.values(grouped);

// ---------- Helpers (same cleaning technique) ----------
const htmlDecode = (s = "") =>
  s
    .replace(/&nbsp;/gi, " ")
    .replace(/&amp;/gi, "&")
    .replace(/&lt;/gi, "<")
    .replace(/&gt;/gi, ">")
    .replace(/&quot;/gi, '"')
    .replace(/&#39;/gi, "'")
    .replace(/&#x2F;/gi, "/");

const htmlToText = (html = "") => {
  if (!html) return "";
  let s = html;
  s = s.replace(/<script[\s\S]*?<\/script>/gi, "")
       .replace(/<style[\s\S]*?<\/style>/gi, "");
  s = s.replace(/<(br|hr)\s*\/?>/gi, "\n")
       .replace(/<\/(p|div|li|h[1-6]|tr|table)>/gi, "\n")
       .replace(/<(p|div|li|h[1-6]|tr|table)[^>]*>/gi, " ");
  s = s.replace(/<\/?[^>]+>/g, "");
  s = htmlDecode(s).replace(/\r/g, "").replace(/[ \t\u00A0]+/g, " ");
  return s;
};

const cutAtFirst = (text, patterns) => {
  let cut = -1;
  for (const re of patterns) {
    const idx = text.search(re);
    if (idx !== -1 && (cut === -1 || idx < cut)) cut = idx;
  }
  return cut === -1 ? text : text.slice(0, cut);
};

const stripSignature = (text) => {
  const sigPatterns = [
    /\n--\s*\n/i,
    /\nSent from my (iPhone|Android).*$/i,
    /\nBest regards[,\s]*\n.*$/i,
    /\nRegards[,\s]*\n.*$/i,
    /\nThanks[,\s]*\n.*$/i
  ];
  const cutIdx = sigPatterns
    .map(re => text.search(re))
    .filter(i => i !== -1)
    .sort((a,b) => a - b)[0];
  return (cutIdx !== undefined) ? text.slice(0, cutIdx) : text;
};

const cleanWhitespace = (s) =>
  s.replace(/\u200B/g, "")
   .replace(/\r/g, "")
   .replace(/[ \t]+/g, " ")
   .replace(/\n{3,}/g, "\n\n")
   .trim();

const firstBodyOnly = (s) => {
  const t = s.replace(/\r\n/g, "\n");
  const cutoffPatterns = [
    /\n\s*On .*? wrote:\s*\n/mi,
    /\n\s*-{2,}\s*Original Message\s*-{2,}\s*\n/mi,
    /\n\s*Begin forwarded message:\s*\n/mi,
    /\n\s*-{2,}\s*Forwarded message\s*-{2,}\s*\n/mi,
    /\n>+/m,
    /\nFrom:\s.*\n(?:.*\n){0,12}?Subject:\s.*\n/mi
  ];
  let newest = cutAtFirst(t, cutoffPatterns);
  newest = cutAtFirst(newest, [
    /<asst@patchbay\.xyz>/i,
    /Forwarded message/i
  ]);
  newest = stripSignature(newest);
  return cleanWhitespace(newest);
};

const pickBody = (email) => {
  const text = (email.text && email.text.trim()) ? email.text : "";
  const html = (!text && email.html) ? email.html : "";
  return text ? text : htmlToText(html);
};

