  // Cleaned latest email body (prefer text, fallback to html)
  let raw = pickBody(data);
  let cleaned = firstBodyOnly(raw);
  // Make single-line (keep if you want compact output)
  const emailText = cleaned.replace(/\n/g, " ").replace(/\s+/g, " ").trim();

