const text = $input.first().json.emailText || "";

// Step 1: Split by "From:" (preserving it)
const parts = text.split("From:");
const emails = [];

for (let i = 0; i < parts.length; i++) {
  let part = parts[i].trim();
  if (!part) continue;

  const email = (i === 0 ? part : "From: " + part);
  emails.push(email);
}

// Step 2: Clean garbage links and noise
const cleanText = (str) => {
  return str
    .replace(/https?:\/\/[^\s<>()"']+/gi, '')                  // Remove normal URLs
    .replace(/www\.[^\s<>()"']+/gi, '')                        // Remove www links
    .replace(/https?:\s*\/\s*\/[^\s]+/gi, '')                  // Remove broken/spaced links
    .replace(/magiclink=.*?(\s|$)/gi, '')                      // Remove mimecast magiclinks
    .replace(/mimecast\.com\S+/gi, '')                         // Remove MIMECAST encoded URLs
    .replace(/[<>]/g, '')                                      // Remove angle brackets
    .replace(/CGBANNERINDICATOR/gi, '')                        // Remove banners
    .replace(/\n\s*>\s*/g, ' ')                                // Remove "\n > " line continuations
    .replace(/\s{2,}/g, ' ')                                   // Collapse multiple spaces
    .trim();
};

// Step 3: Clean each email
const cleanedEmails = emails.map(cleanText);

// Step 4: Chunk into 5 groups
const chunkCount = 5;
const chunkSize = Math.ceil(cleanedEmails.length / chunkCount);

const chunks = [];

for (let i = 0; i < chunkCount; i++) {
  const chunkEmails = cleanedEmails.slice(i * chunkSize, (i + 1) * chunkSize);
  if (chunkEmails.length) {
    chunks.push({ json: { chunk: chunkEmails.join('\n\n') } });
  }
}

return chunks;

const attachements = $("Trigger").item.binary
// Return the result
return [
  {
    json: {
      binary: attachements
    }
  }
];

