const item = $input.item.json;

const senderAddress = $('EmailPath').first().json?.from?.value?.[0]?.address;

// Step 2: Match sender against Managers list
const matchedManager = item.Managers.find(manager => manager.address === senderAddress);

// Step 3: Return response based on match
if (matchedManager) {
  // Destructure to exclude person_number, Clients, and Role
  const { person_number, Role, ...cleanManager } = matchedManager;

  return [
    {
      json: {
        status: true,
        message: "Sender verified as a Patchbay manager.",
        manager: cleanManager
      }
    }
  ];
} else {
  return [
    {
      json: {
        status: false,
        address: senderAddress,
        message: "We couldn't verify you as a Patchbay user. If you're not yet registered, please visit https://hub.patchbay.xyz/account/signup to sign up and get started." + senderAddress
      }
    }
  ];
}

const assistantEmail = 'asst@patchbay.xyz';

return items.map(item => {
  const data = item.json;

  const from = data.from?.value?.[0]?.address || '';
  const toList = data.to?.value?.map(e => e.address.toLowerCase()) || [];
  const ccList = data.cc?.value?.map(e => e.address.toLowerCase()) || [];
  const bccList = data.bcc?.value?.map(e => e.address.toLowerCase()) || [];

  const toIncludesAssistant = toList.includes(assistantEmail);
  const ccIncludesAssistant = ccList.includes(assistantEmail);
  const bccIncludesAssistant = bccList.includes(assistantEmail);
  const totalToRecipients = toList.length;

  let routing = 'unclassified';

  if (from === assistantEmail) {
    routing = 'self';
  } else if (toIncludesAssistant && totalToRecipients > 1) {
    routing = 'bot_and_others';
  } else if (toIncludesAssistant && totalToRecipients === 1) {
    routing = 'bot_only';
  } else if (ccIncludesAssistant || bccIncludesAssistant) {
    routing = 'bot_ccd_bccd';
  }

  return {
    json: {
      ...data,
      routing,
    },
    binary: item.binary || {}
  };
});

const thread = $("UpdateSession").item.json;
let newManagers = $input.item.json;

// Ensure Managers key exists
if (!Array.isArray(thread.Managers)) {
  thread.Managers = [];
}

// Normalize to array
if (!Array.isArray(newManagers)) {
  newManagers = [newManagers];
}

// Collect existing user_ids
const existingIds = new Set(thread.Managers.map(m => m.address));

// Append only if user_id not already present
newManagers.forEach(manager => {
  if (!existingIds.has(manager.address)) {
    thread.Managers.push(manager);
  }
});

return { json: thread };

