const pickBody = (email) => {
  // Prefer plain text if present; otherwise, convert HTML
  const text = (email.text && email.text.trim()) ? email.text : "";
  const html = (!text && email.html) ? email.html : "";
  return text ? text : htmlToText(html);
};

const extractAddresses = (field) =>
  (lastEmail?.[field]?.value || [])
    .map(e => (e && typeof e === "object") ? e.address : e)
    .filter(Boolean);

// Helper to format email with full headers
const formatEmailText = (email) => {
  // Format From
  const fromArray = email?.from?.value || [];
  const fromFormatted = fromArray.length > 0
    ? `${fromArray[0].name} (${fromArray[0].address})`
    : '';

  // Format To
  const toArray = email?.to?.value || [];
  const toFormatted = toArray
    .map(t => `${t.name} (${t.address})`)
    .join(', ');

  // Format CC (filtered)
  const ccArray = email?.cc?.value || [];
  const filteredCC = ccArray.filter(cc => cc.address !== 'asst@patchbay.xyz');
  const ccFormatted = filteredCC
    .map(c => `${c.name} (${c.address})`)
    .join(', ');

  // Format Date
  const dateFormatted = email.date 
    ? new Date(email.date).toLocaleString('en-US', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: '2-digit',
        hour12: true
      })
    : '';

  // Get the email body
  let raw = pickBody(email);
  let emailBody = firstBodyOnly(raw);

  // Build formatted email text
  let formattedEmail = `From: ${fromFormatted}\n`;
  formattedEmail += `To: ${toFormatted}\n`;
  if (ccFormatted) {
    formattedEmail += `CC: ${ccFormatted}\n`;
  }
  formattedEmail += `Date: ${dateFormatted}\n`;
  formattedEmail += `Subject: ${email.subject || ''}\n`;
  formattedEmail += `Body:\n${emailBody}`;

  return formattedEmail;
};

// ---------- Build result ----------
const emailText = formatEmailText(lastEmail);

// Create the base email object structure
const baseEmailObject = {
  ThreadId: lastEmail.threadId || null,
  email_text: emailText,
  from: extractAddresses("from"),
  to: extractAddresses("to"),
  cc: extractAddresses("cc"),
  bcc: extractAddresses("bcc")
};

// If managers list is empty or not provided, return the original behavior
if (!Array.isArray(managersList) || managersList.length === 0) {
  return [
    {
      json: {
        ...baseEmailObject,
        manager: $input.first().json.manager
      }
    }
  ];
}

// Create structured object for each manager in the list
return managersList.map(manager => ({
  json: {
    ...baseEmailObject,
    manager: manager
  }
}));

// Parse, extract base64 PDF, attach as binary
// Works with multiple incoming items.

const items = $input.all();

function safeParseSession(value) {
  if (value && typeof value === 'object') return value;
  if (typeof value === 'string') {
    try {
      return JSON.parse(value);
    } catch (err) {
      return { __parse_error: err.message, __raw: value };
    }
  }
  return { __parse_error: 'Unsupported user_session type', __raw: value };
}

const outputs = [];
for (const item of items) {
  const parsedSession = safeParseSession(item.json.user_session);

  const out = {
    json: {
      ...item.json,
      user_session: parsedSession,
    },
  };

  // Try to pull base64 from user_session.invoice_metadata.pdf_invoice_content
  const pdfB64 = parsedSession?.invoice_metadata?.pdf_invoice_content;

  if (typeof pdfB64 === 'string' && pdfB64.trim().length > 0) {
    try {
      const buf = Buffer.from(pdfB64.trim(), 'base64');

      // Name the file using invoice_id if available
      const invoiceId = parsedSession?.invoice_metadata?.invoice_id ?? 'invoice';
      const fileName = `invoice_${invoiceId}.pdf`;

      // Prepare binary for n8n
      const binary = await this.helpers.prepareBinaryData(
        buf,
        fileName,
        'application/pdf'
      );

      out.binary = { pdf_invoice: binary };

      // Optional: remove the large base64 string from JSON to keep items light
      try {
        if (out.json.user_session?.invoice_metadata) {
          delete out.json.user_session.invoice_metadata.pdf_invoice_content;
        }
      } catch (_) {}
    } catch (err) {
      out.json.__binary_error = `Failed to decode PDF: ${err.message}`;
    }
  } else {
    out.json.__binary_error = 'pdf_invoice_content not found or not a string';
  }

  outputs.push(out);
}

return outputs;

