parameters.inputSchema:
{
  "title": "PublishingDealExtraction",
  "description": "Schema for extracting structured data from publishing deal contracts",
  "type": "object",
  "properties": {
    "active": {
      "type": ["boolean", "null"],
      "description": "True if deal is signed AND current date is within term dates"
    },
    "execution_date": {
      "type": ["string", "null"],
      "format": "date",
      "description": "Date agreement was fully executed. Format: YYYY-MM-DD"
    },
    "reversion_period": {
      "type": ["number", "null"],
      "description": "Years until rights revert to writer"
    },
    "reversion_type": {
      "type": ["string", "null"],
      "enum": ["Life Of Copyright", "Full Reversion", "Partial Reversion", "Years from Execution", "Reversion"]
    },
    "status": {
      "type": "string",
      "enum": ["Needs Review", "Counterparty Action", "Client Signature", "Counterparty Signature", "Fully Executed", "Terminated"]
    },
    "publishing_deal_type": {
      "type": "string",
      "enum": ["Co-Publishing", "Administration", "Self-Published"]
    },
    "original_file_name": {
      "type": "string",
      "description": "Original filename of uploaded document"
    },
    "territories": {
      "type": ["array", "null"],
      "items": {"type": "string"},
      "description": "List of territories. Null if worldwide"
    },
    "is_worldwide": {
      "type": "boolean",
      "description": "True if deal covers worldwide territory"
    },
    "type": {
      "type": "string",
      "const": "Publishing"
    },
    "terms": {
      "type": "array",
      "description": "Contractual terms in order. Index 0 = initial term, index ≥1 = option periods. Options inherit previous term's values unless explicitly stated otherwise. Look for 'Option period' terms in the document.",
      "items": {
        "type": "object",
        "properties": {
          "index_number": {
            "type": "integer",
            "minimum": 0,
            "description": "0 = initial term, 1+ = option periods"
          },
          "term_length": {
            "type": ["integer", "null"],
            "description": "Duration in units specified by term_length_type"
          },
          "term_length_type": {
            "type": ["string", "null"],
            "enum": ["Years", "Months", "Date"]
          },
          "term_start_date": {
            "type": ["string", "null"],
            "format": "date",
            "description": "Format: YYYY-MM-DD"
          },
          "delivery_requirement_basis": {
            "type": ["string", "null"],
            "enum": ["Albums", "Releases", "Compositions"]
          },
          "delivery_requirement_units": {
            "type": ["integer", "null"],
            "description": "Number of units required"
          },
          "payment_schedule": {
            "type": ["string", "null"],
            "description": "When advance payments are made"
          },
          "currency": {
            "type": ["string", "null"],
            "enum": ["USD", "EUR", "GBP", "CAD", "AUD", "JPY"]
          },
          "advance": {
            "type": ["number", "null"],
            "description": "Advance payment amount (numeric only)"
          },
          "term_status": {
            "type": ["string", "null"],
            "enum": ["Completed", "Pending", "In Progress", "None"]
          },
          "performance_split": {
            "type": ["number", "null"],
            "minimum": 0.0,
            "maximum": 1.0,
            "multipleOf": 0.01,
            "description": "Writer's performance income share as decimal (0.50 = 50%)"
          },
          "sync_split": {
            "type": ["number", "null"],
            "minimum": 0.0,
            "maximum": 1.0,
            "multipleOf": 0.01,
            "description": "Writer's sync income share as decimal"
          },
          "mechanical_split": {
            "type": ["number", "null"],
            "minimum": 0.0,
            "maximum": 1.0,
            "multipleOf": 0.01,
            "description": "Writer's mechanical income share as decimal"
          },
          "other_pub_split": {
            "type": ["number", "null"],
            "minimum": 0.0,
            "maximum": 1.0,
            "multipleOf": 0.01,
            "description": "Writer's other publishing income share as decimal"
          },
          "composition_agreements": {
            "type": "array",
            "items": {"type": "object"},
            "default": []
          }
        },
        "required": ["index_number", "composition_agreements"]
      },
      "minItems": 1
    },
    "publisher": {
      "type": "object",
      "description": "Extract only the core publisher name (e.g., 'SONY/ATV TUNES'). Omit legal suffixes (LLC, Inc.)",
      "properties": {
        "name": {"type": "string"},
        "number": {
          "type": ["string", "null"],
          "format": "uuid"
        }
      },
      "required": ["name", "number"]
    },
    "administrator": {
      "type": ["object", "null"],
      "properties": {
        "name": {"type": "string"},
        "number": {
          "type": ["string", "null"],
          "format": "uuid"
        }
      },
      "required": ["name", "number"]
    },
    "person": {
      "type": "object",
      "properties": {
        "name": {"type": "string"},
        "number": {
          "type": ["string", "null"],
          "format": "uuid"
        }
      },
      "required": ["name", "number"]
    }
  },
  "required": [
    "status",
    "publishing_deal_type",
    "original_file_name",
    "is_worldwide",
    "type",
    "terms",
    "publisher",
    "person"
  ]
}
