// n8n Function node
// Input: last 10 message objects, newest at index 0
// Output: { json: { markdown: "<markdown string>" } }

const WINDOW_SIZE = 10;

// ---- Helpers ----
const toStr = (v) => (v === null || v === undefined) ? "" : String(v);

const stripHtml = (s) => {
  const str = toStr(s);
  return str
    .replace(/<style[\s\S]*?<\/style>/gi, " ")
    .replace(/<script[\s\S]*?<\/script>/gi, " ")
    .replace(/<[^>]*>/g, " ")
    .replace(/\s+/g, " ")
    .trim();
};

const decodeHtml = (s) => {
  const map = {
    "&nbsp;": " ",
    "&lt;": "<",
    "&gt;": ">",
    "&amp;": "&",
    "&quot;": "\"",
    "&#39;": "'",
  };
  return toStr(s).replace(/(&nbsp;|&lt;|&gt;|&amp;|&quot;|&#39;)/g, (m) => map[m] || m);
};

const htmlToText = (html) => stripHtml(decodeHtml(html));

const safeJsonParse = (val) => {
  if (typeof val !== "string") return null;
  const t = val.trim();
  if (!(t.startsWith("{") || t.startsWith("["))) return null;
  try { return JSON.parse(t); } catch { return null; }
};

const formatTime = (raw) => {
  if (!raw) return "unknown";
  // If it's already a readable string (e.g., "2025-09-05 10:23:44"), keep it.
  // Otherwise try to parse and convert to ISO.
  const d = new Date(raw);
  if (isNaN(d.getTime())) return toStr(raw).trim();
  // Use local-like ISO without milliseconds for readability
  const iso = d.toISOString().replace("T", " ").replace(".000Z", "Z");
  return iso;
};

// ---- Collect messages (use order as provided; newest first) ----
let messages = [];
if (items.length === 1) {
  const j = items[0].json ?? {};
  if (Array.isArray(j)) messages = j;
  else if (Array.isArray(j.data)) messages = j.data;
  else if (Array.isArray(j.messages)) messages = j.messages;
  else if (j.id && j.message) messages = [j];
} else {
  for (const it of items) {
    const j = it.json ?? {};
    if (j.id && j.message) messages.push(j);
  }
}

if (!messages.length) {
  return [{ json: { history: "Missing" } }];
}

// ---- Keep window of 10 (already newest-first), then switch to chronological for pairing ----
const windowMsgs = messages.slice(0, WINDOW_SIZE);
const chronological = windowMsgs.slice().reverse(); // oldest -> newest

// ---- Build user→AI turns (with timestamps) ----
const turns = [];
for (let i = 0; i < chronological.length; i++) {
  const msg = chronological[i];
  if (msg?.message?.type === "human") {
    const userRaw = msg.message.content ?? "";
    const userText = stripHtml(userRaw);

    let aiText = null;

    // Look ahead for next AI reply within the window
    for (let j = i + 1; j < chronological.length; j++) {
      const next = chronological[j];
      const type = next?.message?.type;
      if (type === "ai") {
        const raw = next.message.content ?? "";
        const parsed = safeJsonParse(raw);
        const responseHtml = parsed?.output?.response_html ?? null;
        aiText = responseHtml ? htmlToText(responseHtml) : stripHtml(raw);
        break;
      }
      if (type === "human") break; // next human starts a new turn
    }

    turns.push({
      userText,
      aiText,
    });
  }
}

// ---- Build Markdown string ----
let md = `### Conversation (last ${Math.min(WINDOW_SIZE, turns.length)} turns)\n\n`;
turns.forEach((t) => {
  md += `**User** ${t.userText}\n\n`;
  if (t.aiText) {
    md += `**AI** ${t.aiText}\n\n`;
  } else {
    md += `**AI** (no reply found)_\n\n`;
  }
});

return [{
  json: {
    history: md.trim()
  }
}];

// n8n Code node (JavaScript)
// Creates two DB-ready objects (human, then ai) with cleaned content
// Adds "Last interaction: <date time>" to the human message content
// Hardened for Postgres: escapes backslashes and invalid \u escapes, removes null bytes, fixes unpaired surrogates.

// ---------------- Helpers ----------------
const toStr = (v) => (v === null || v === undefined) ? "" : String(v);

/** Strip HTML to plain text and tidy whitespace */
function stripHtml(html) {
  const s = toStr(html);
  const withoutTags = s.replace(/<[^>]*>/g, " ");
  const decoded = withoutTags
    .replace(/&nbsp;/gi, " ")
    .replace(/&amp;/gi, "&")
    .replace(/&lt;/gi, "<")
    .replace(/&gt;/gi, ">")
    .replace(/&#39;/gi, "'")
    .replace(/&quot;/gi, '"');
  return decoded.replace(/\s+/g, " ").trim();
}

/** Try to parse JSON safely */
function tryParseJson(s) {
  if (typeof s !== "string") return null;
  const trimmed = s.trim();
  if (!(trimmed.startsWith("{") || trimmed.startsWith("["))) return null;
  try { return JSON.parse(trimmed); } catch { return null; }
}

/** Remove null bytes and fix unpaired surrogate code units */
function sanitizeUnicode(s) {
  let out = s.replace(/\u0000/g, ""); // remove NULs
  // remove unpaired surrogates
  out = out.replace(/[\uD800-\uDBFF](?![\uDC00-\uDFFF])|(?<![\uD800-\uDBFF])[\uDC00-\uDFFF]/g, "�");
  return out;
}

/** Escape content so Postgres won't treat \u... as a Unicode escape */
function escapeForPostgresText(s) {
  // First, double all backslashes
  let out = s.replace(/\\/g, "\\\\");
  // If any sequence looks like \u but not \uXXXX, re-escape it (turn \\u into \\\\u in the final string)
  out = out.replace(/\\u(?![0-9a-fA-F]{4})/g, "\\\\u");
  return out;
}

/** Normalize AI/Human content */
function normalizeContent(raw) {
  const asString = typeof raw === "string" ? raw : JSON.stringify(raw ?? "");
  const maybeJson = tryParseJson(asString);

  if (maybeJson && typeof maybeJson === "object") {
    const respHtml =
      maybeJson?.output?.response_html ??
      maybeJson?.response_html ??
      maybeJson?.html ??
      null;

    if (respHtml) return stripHtml(respHtml);

    if (typeof maybeJson.content === "string") {
      const c = maybeJson.content.trim();
      return /<[^>]+>/.test(c) ? stripHtml(c) : c;
    }
    return JSON.stringify(maybeJson);
  }

  return /<[^>]+>/.test(asString) ? stripHtml(asString) : asString.trim();
}

/** Build one DB record */
function buildRecord({ tag, content, session_id, id = null }) {
  const type = (toStr(tag).toLowerCase() === "human") ? "human" : "ai";
  let cleaned = normalizeContent(content);

  // Add last interaction timestamp only for human
  if (type === "human") {
    const ts = new Date().toISOString(); // ISO 8601 (UTC)
    cleaned += `\n\nLast interaction: ${ts}`;
  }

  // Unicode + Postgres safety
  cleaned = sanitizeUnicode(cleaned);
  cleaned = escapeForPostgresText(cleaned);

  return {
    json: {
      session_id: toStr(session_id),
      message: {
        type,
        content: cleaned,
        tool_calls: [],
        additional_kwargs: {},
        response_metadata: {},
        invalid_tool_calls: []
      }
    }
  };
}

// ---------------- Input Normalization ----------------
const inJson = $json ?? {};
let humanRec = null;
let aiRec = null;

// Case 1: records array
if (Array.isArray(inJson.records)) {
  for (const r of inJson.records) {
    const tag = toStr(r?.tag).toLowerCase();
    if (tag === "human") humanRec = { ...r };
    if (tag === "ai") aiRec = { ...r };
  }
}

// Case 2: explicit human/ai objects
if (!humanRec && inJson.human) {
  humanRec = {
    tag: "human",
    content: inJson.human.content,
    session_id: inJson.human.session_id ?? inJson.session_id
  };
}
if (!aiRec && inJson.ai) {
  aiRec = {
    tag: "ai",
    content: inJson.ai.content,
    session_id: inJson.ai.session_id ?? inJson.session_id
  };
}

// Case 3: separate fields
if (!humanRec && (inJson.human_content || inJson.human_text)) {
  humanRec = {
    tag: "human",
    content: inJson.human_content ?? inJson.human_text,
    session_id: inJson.session_id
  };
}
if (!aiRec && (inJson.ai_content || inJson.ai_text)) {
  aiRec = {
    tag: "ai",
    content: inJson.ai_content ?? inJson.ai_text,
    session_id: inJson.session_id
  };
}

// Case 4: fallback
if (!humanRec && !aiRec && inJson.tag && inJson.content) {
  const tag = toStr(inJson.tag).toLowerCase();
  if (tag === "human") humanRec = { tag: "human", content: inJson.content, session_id: inJson.session_id };
  if (tag === "ai") aiRec = { tag: "ai", content: inJson.content, session_id: inJson.session_id };
}

// Session fallback
const sessionFallback = humanRec?.session_id ?? aiRec?.session_id ?? inJson.session_id ?? "";
if (humanRec && !humanRec.session_id) humanRec.session_id = sessionFallback;
if (aiRec && !aiRec.session_id) aiRec.session_id = sessionFallback;

// ---------------- Build Output ----------------
const out = [];
if (humanRec) out.push(buildRecord(humanRec));
if (aiRec) out.push(buildRecord(aiRec));

if (out.length === 0) {
  throw new Error("No valid inputs found. Provide `records`, or human/ai objects, or content fields.");
}

return out;

