{
  "type": "object",
  "required": ["response", "task_attempted", "next_task"],
  "properties": {
    "response": {
      "type": "string",
      "description": "A well structured response to the manager based on the tool executed. Strictly follow gmail-API friendly html formatting with propoer bullets, numbering etc.",
      "minLength": 1
    },
    "task_attempted": {
      "type": "object",
      "required": ["task_name", "task_status"],
      "properties": {
        "task_name": {
          "type": "string",
          "description": "The name of the last task attempted by you on behalf of the manager.",
          "enum": ["create_deal", "update_deal", "create_invoice", "update_invoice", "file_requested"]
        },
        "task_status": {
          "type": "string",
          "description": "Status of the last task.",
          "enum": ["completed", "failed"]
        }
      }
    },
    "next_task": {
      "type": "string",
      "description": "Any next task proposed by you to the manager. Use 'no_action' if no task is recommended or the task_attempted failed.",
      "enum": ["create_deal", "update_deal", "create_invoice", "update_invoice", "no_action", "send_file_to_user", "send_file_on_cc", "retry"]
    }
  },
  "allOf": [
    {
      "if": {
        "properties": {
          "task_attempted": {
            "properties": {
              "task_status": { "const": "failed" }
            },
            "required": ["task_status"]
          }
        }
      },
      "then": {
        "properties": {
          "next_task": { "const": "no_action" }
        }
      }
    }
  ]
}

