const binaryData = $("Trigger").item.json.binary;
const allowedTypes = ['pdf', 'doc', 'docx'];

const entries = Object.entries(binaryData || {}).reverse();
const results = [];

for (const [filename, fileData] of entries) {
  if (allowedTypes.includes(fileData.fileExtension)) {
    results.push({
      json: {
        filename: filename,
        fileType: fileData?.fileType || fileData?.fileExtension,
        status: true,
        orgFilename: fileData.fileName,
        file: fileData,

      },
      binary: {
        target_file: {
          ...fileData,
          fileName: filename
        }
      }
    });
  }
}

// Return matching files, or fallback if none found
if (results.length > 0) {
  return results;
} else {
  return [
    {
      json: {
        message: "No valid attachment found",
        status: false
      }
    }
  ];
}

