// n8n Node.js Code for Client Management
// This code should be used in an n8n "Code" node

// Get input data from previous node
const inputData = $input.all();
const data = inputData[0].json.data;

// Extract the required components
const sessionManager = data.session_manager;
const sessionData = data.sessioin_data; // Note: keeping original typo from input
const clientInfo = data.client;

// Function to add client to manager's client list
function addClientToManager(sessionData, sessionManager, clientInfo) {
  // Make a deep copy to avoid modifying original data
  const updatedSessionData = JSON.parse(JSON.stringify(sessionData));

  // Find the manager in the managers list
  const managerIndex = updatedSessionData.Managers.findIndex(
    manager => manager.user_id === sessionManager.user_id
  );

  if (managerIndex === -1) {
    throw new Error(`Manager with user_id ${sessionManager.user_id} not found in managers list`);
  }

  // Get the manager's current clients list
  const manager = updatedSessionData.Managers[managerIndex];

  // Initialize Clients array if it doesn't exist
  if (!manager.Clients) {
    manager.Clients = [];
  }

  // Check if client already exists in the manager's client list
  const clientExists = manager.Clients.some(client => {
    // Check by book_number (primary identifier) or person_name as fallback
    return (clientInfo.book_number && client.book_number === clientInfo.book_number) ||
           (client.person_name === clientInfo.person_name);
  });

  // If client doesn't exist, add them to the list
  if (!clientExists) {
    // Create client object with all provided information
    const newClient = {
      person_name: clientInfo.person_name,
      performer_name: clientInfo.performer_name,
      status: clientInfo.status,
      book_number: clientInfo.book_number,
      book_id: clientInfo.book_id,
      role: clientInfo.role,
      message: clientInfo.message,
      verification_status: clientInfo.verification_status
    };

    // Add the new client to the manager's clients list
    updatedSessionData.Managers[managerIndex].Clients.push(newClient);

    console.log(`Client ${clientInfo.person_name} added to manager ${sessionManager.name}'s client list`);
  } else {
    console.log(`Client ${clientInfo.person_name} already exists in manager ${sessionManager.name}'s client list`);
  }

  return updatedSessionData;
}

// Execute the function
try {
  const updatedSessionData = addClientToManager(sessionData, sessionManager, clientInfo);

  // Return session data with success status
  return [{ 
    json: {
      sessionData: updatedSessionData,
      status: true
    }
  }];

} catch (error) {
  // Handle errors gracefully
  console.error('Error processing client management:', error.message);

  // Return original session data with failure status
  return [{ 
    json: {
      sessionData: sessionData,
      status: false
    }
  }];
}

const input = $input.item.json.data || {};
// Extract data from the new structure
const toEmails = (input.recipients || []).join(', ');
const ccEmails = (input.cc || []).join(', '); // Fixed: Added missing (
const messageId = input.MessageId || '<no-message-id>';
const references = (input.References || []).join(' ');
const subject = input.subject || 'No Subject'; // Fixed: Changed Subject to subject
const threadId = input.ThreadId || "";
const body = input.message || '';
// Compose MIME message with HTML content
let mime = `To: ${toEmails}`;
// Add CC if provided
if (ccEmails) {
  mime += `\nCc: ${ccEmails}`;
}
mime += `\nSubject: ${subject}
In-Reply-To: ${messageId}
References: ${references}
Content-Type: text/html; charset=UTF-8

${body}`;
// Encode to base64url
const raw = Buffer.from(mime)
  .toString('base64')
  .replace(/\+/g, '-')
  .replace(/\//g, '_')
  .replace(/=+$/, '');
// Create the response with email payload
const response = {
  email_payload: {
    raw,
    mime,
    threadId,
    subject,
    to: toEmails,
    ...(ccEmails && { cc: ccEmails })
  }
};
// Return the result
return { json: response };
